(58, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'pending', 'woocommerce.com', '2023-11-21 18:46:38', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2023-11-21 18:46:38', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-11-21 18:46:38', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'З’єднатися з WooCommerce.com', 'Підключайтеся, щоб отримувати важливі сповіщення про товар та оновлення.', '{}', 'unactioned', 'woocommerce-admin', '2023-11-21 18:46:39', NULL, 0, 'plain', '', 0, 1, 'info');
